CREATE TABLE bill (id INTEGER PRIMARY KEY AUTOINCREMENT, date DATETIME NOT NULL, place VARCHAR(255) NOT NULL, totalprice DOUBLE, carid INTEGER NOT NULL, userid INTEGER NOT NULL, type VARCHAR(255), fueltypeid INTEGER, liters DOUBLE, priceforliter DOUBLE, distancetilllastbill DOUBLE, description LONGTEXT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE car (id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(255) NOT NULL, model VARCHAR(255) NOT NULL, brand VARCHAR(255) NOT NULL, year INTEGER, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE car2_user (id INTEGER PRIMARY KEY AUTOINCREMENT, carid INTEGER NOT NULL, userid INTEGER NOT NULL);
CREATE TABLE fuel_type (id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(255) NOT NULL, fueltankrate DOUBLE);
CREATE TABLE user (id INTEGER PRIMARY KEY AUTOINCREMENT, username VARCHAR(255) NOT NULL, password VARCHAR(255) NOT NULL, lastlogin DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE INDEX bill_type_idx ON bill (type);
