CREATE TABLE car (id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(255) NOT NULL, model VARCHAR(255) NOT NULL, brand VARCHAR(255) NOT NULL, year INTEGER, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
CREATE TABLE car2_user (id INTEGER PRIMARY KEY AUTOINCREMENT, car_id INTEGER NOT NULL, user_id INTEGER NOT NULL);
CREATE TABLE user (id INTEGER PRIMARY KEY AUTOINCREMENT, username VARCHAR(255) NOT NULL, password VARCHAR(255) NOT NULL, lastlogin DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL);
